package com.irets.logging.domain;public class Syslog {			private String host;	private int port;	private String facility;	private String threshold;		public void setHost(String host) {		this.host = host;	}		public String getHost() {		return host;	}		public void setPort(int port) {		this.port = port;	}		public int getPort() {		return port;	}		public void setFacility(String facility) {		this.facility = facility;	}		public String getFacility() {		return facility;	}		public void setThreshold(String threshold) {		this.threshold = threshold;	}		public String getThreshold() {		return threshold;	}}